import java.util.Scanner;


public class Loopexample {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub

	for(int i=1; i<=5; i++){
		
/*for(int *name the variable*; *state the length of the loop*
 * name the function
 */

// we chose the first variable i which is 1; i<=5 means that
// i can't be larger or equal to 5; 
//with statement i++ we increase i for +1 each loop
		System.out.println(i);
	}
	
	int e=1;
	while(e<=5){
		System.out.println(e);
		e++;
	}
	
	int b=1;
	do{
		System.out.println(b);
		b++;
	}while(b<=10);
	}
	
	}
	


